import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import "./style/Expenses.css";
import { useState } from "react";

const Expenses = (props) => {
  const initialExpensesItems = props.expensesData.map(
    (expenseData, indexes) => (
      <ExpenseItem
        key={expenseData.id}
        // key={indexes}
        title={expenseData.title}
        amount={expenseData.amount}
        date={expenseData.date}
      />
    )
  );
  const [filteredYear, setFilteredYear] = useState("2020");
  const [expenseItems, setExpenseItems] = useState(initialExpensesItems);

  const FilterChangeHandler = (selectedYear) => {
    console.log(selectedYear);
    setFilteredYear(selectedYear);
  };
  const filteredExpenses = props.expensesData.filter(
    (expense) => expense.date.getFullYear().toString() === filteredYear
  );
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          defaultYear={filteredYear}
          onFilterChange={FilterChangeHandler}
        />
        {filteredExpenses.map((expenseData, indexes) => (
          <ExpenseItem
            key={expenseData.id}
            // key={indexes}
            title={expenseData.title}
            amount={expenseData.amount}
            date={expenseData.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
