import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import "./style/Expenses.css";
import { useState } from "react";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("All");
  const [allYears, setAllYears] = useState("All");

  const FilterChangeHandler = (selectedYear) => {
    console.log(selectedYear);
    setFilteredYear(selectedYear);
  };
  const filteredExpenses = props.expensesData.filter(
    (expense) => expense.date.getFullYear().toString() === filteredYear
  );

  let filteredExpenseItems = <p>No Expenses found!</p>;
  if (condition) {
    setAllYears(
      props.expensesData.map((expenseData, indexes) => (
        <ExpenseItem
          key={expenseData.id}
          // key={indexes}
          title={expenseData.title}
          amount={expenseData.amount}
          date={expenseData.date}
        />
      ))
    );
  }
  if (filteredExpenses.length) {
    filteredExpenseItems = filteredExpenses.map((expenseData, indexes) => (
      <ExpenseItem
        key={expenseData.id}
        // key={indexes}
        title={expenseData.title}
        amount={expenseData.amount}
        date={expenseData.date}
      />
    ));
  }

  let displayedExpenseItems = filteredExpenseItems;

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          defaultYear={filteredYear}
          onFilterChange={FilterChangeHandler}
        />
        {filteredExpenseItems}
      </Card>
    </div>
  );
};

export default Expenses;

