import Chart from "../Charts/Chart";

const ExpensesChart = (props) => {
  let ChartDataPoints;
  if (props.chosenYear === "All") {
    ChartDataPoints = [
      { label: 2019, value: 0 },
      { label: 2020, value: 0 },
      { label: 2021, value: 0 },
      { label: 2022, value: 0 },
      { label: 2023, value: 0 },
    ];
    let amount2019 = 0;
    let amount2020 = 0;
    let amount2021 = 0;
    let amount2022 = 0;
    let amount2023 = 0;
    let totalAmount = 0;
    for (const expense of props.allExpenses) {
