        }
      }
      if (expense.date.getFullYear() === ChartDataPoints[2].label)
        amount2021 += expense.amount;
      else totalAmount += expense.amount;
      // console.log(expense.date.getFullYear());
    }
    console.log(ChartDataPoints[0].label);
    console.log(props.allExpenses);
    console.log(amount2021);
    console.log(totalAmount);
  } else {
    ChartDataPoints = [
      { label: "Jan", value: 0 },
      { label: "Feb", value: 0 },
      { label: "Mar", value: 0 },
      { label: "Apr", value: 0 },
      { label: "May", value: 0 },
      { label: "Jun", value: 0 },
      { label: "Jul", value: 0 },
      { label: "Aug", value: 0 },
      { label: "Sep", value: 0 },
      { label: "Oct", value: 0 },
      { label: "Nov", value: 0 },
      { label: "Dec", value: 0 },
    ];
    for (const expense of props.yearExpenses) {
      const expenseMonth = expense.date.getMonth();
      ChartDataPoints[expenseMonth].value += expense.amount;
    }
  }
  return <Chart dataPoints={ChartDataPoints} />;
};

export default ExpensesChart;
