import { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
  // const [enteredTitle, setEnteredTitle] = useState("");
  // const [enteredAmount, setEnteredAmount] = useState("");
  // const [enteredDate, setEnteredDate] = useState("");

  const [userInput, setUserInput] = useState({
    title: "",
    amount: "",
    date: "",
  });

  const titleChangeHandler = (event) => {
    // setEnteredTitle(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredTitle: event.target.value,
    // });
    /*
     */
    /* use this form to always ensure that you are
      using the latest version of the state.
      As the useState() is scheduled */
    setUserInput((prevState) => {
      console.log(event.target.value);
      return { ...prevState, title: event.target.value };
    });
  };
  const amountChangeHandler = (event) => {
    // setEnteredAmount(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredAmount: event.target.value,
    // });
    /*
     */
    /* use this form to always ensure that you are
      using the latest version of the state.
      As the useState() is scheduled */
    setUserInput((prevState) => {
      return { ...prevState, amount: event.target.value };
    });
  };
  const dateChangeHandler = (event) => {
    // setEnteredDate(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredDate: event.target.value,
    // });
    /*
     */
    /* use this form to always ensure that you are
      using the latest version of the state.
      As the useState() is scheduled */
    setUserInput((prevState) => {
      const dateObject = new Date(event.target.value);
      return { ...prevState, date: dateObject };
    });
  };

  const submitHandler = (e) => {
    e.preventDefault();

    const expenseData = userInput;
    console.log(expenseData);
    setUserInput(() => {
      userInput.title = "";
    });
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input value={title} onChange={titleChangeHandler} type="text" />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            value={amount}
            onChange={amountChangeHandler}
            type="number"
            min="0.01"
            step="0.01"
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            value={date}
            onChange={dateChangeHandler}
            type="date"
            min="2019-01-01"
            max="2023-6-30"
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
