import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import "./style/Expenses.css";
import { useState } from "react";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const [expenseItems, setExpenseItems] = useState(initialExpensesItems);
  const initialExpensesItems = props.expensesData.map(
    (expenseData, indexes) => (
      <ExpenseItem
        key={expenseData.id}
        // key={indexes}
        title={expenseData.title}
        amount={expenseData.amount}
        date={expenseData.date}
      />
    )
  );

  const FilterChangeHandler = (selectedYear) => {
    console.log(selectedYear);
    const filteredExpenses = props.expensesData.filter(
      (expense) => expense.date.getFullYear().toString() === selectedYear
    );
    console.log(filteredExpenses);
    setFilteredYear(selectedYear);
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          defaultYear={filteredYear}
          onFilterChange={FilterChangeHandler}
        />
        {expenseItems}
        {/* {props.expensesData.map((expenseData, indexes) => (
          <ExpenseItem
            key={expenseData.id}
            // key={indexes}
            title={expenseData.title}
            amount={expenseData.amount}
            date={expenseData.date}
          />
        ))} */}
      </Card>
    </div>
  );
};

export default Expenses;

